<?php if (!empty($groups)) { ?>	<?php $group_counter = 0; ?>	<ul class="nav nav-tabs nav-center">		<?php foreach($groups as $group) { ?>			<li style="text-align: center" class="<?php echo $group_counter++ === 0 ? "active" : ""; ?>">				<?php if (!empty($group["icon"])) { ?>					<a href="#{VAR:prefix}<?php echo $group["id"]; ?>" data-toggle="tab"><img src="<?php echo $group["icon"]; ?>" border="0"><br />				<?php } ?>				<?php echo $group["caption"]; ?></a>			</li>		<?php } ?>	</ul>	<?php if (isset($popups)) { echo $popups; } ?>	<div class="tab-content horizontal-padding-15">	<?php $group_counter = 0; ?>	<?php foreach($groups as $group) { ?>		<div class="tab-pane <?php echo $group_counter++ === 0 ? "active" : ""; ?> tabbable tabs-left" id="{VAR:prefix}<?php echo $group["id"]; ?>">			<?php if (!empty($group["subgroups"])) { ?>				<ul class="nav nav-tabs">					<?php $subgroup_counter = 0; ?>					<?php foreach($group["subgroups"] as $subgroup) { ?>						<li class="<?php echo $subgroup_counter++ === 0 ? "active" : ""; ?>">							<a href="#{VAR:prefix}<?php echo $subgroup["id"]; ?>" data-toggle="tab">								<i class="icon-<?php echo $subgroup["icon"]; ?>"></i> <?php echo $subgroup["caption"]; ?>							</a>						</li>					<?php } ?>				</ul>				<div class="tab-content">					<?php $subgroup_counter = 0; ?>					<?php foreach($group["subgroups"] as $subgroup) { ?>					<div class="tab-pane <?php echo $subgroup_counter++ === 0 ? "active" : ""; ?>" id="{VAR:prefix}<?php echo $subgroup["id"]; ?>">						<?php echo aw_modal::parse_group($subgroup); ?>					</div>					<?php } ?>				</div>			<?php } else { ?>				<?php echo aw_modal::parse_group($group); ?>			<?php } ?>		</div>	<?php } ?>	</div><?php } else { ?>	<div class="horizontal-padding-15" id="{VAR:prefix}<?php echo $groupless["id"]; ?>">		<?php echo aw_modal::parse_group($groupless); ?>	</div><?php } ?>